number = [ int(n) for n in str(num)]

from functools import reduce
import time
# define a lamba function that will calculate the product of the elements within a list using reduce
def prod(list):
    return reduce(lambda x, y: int(x) * int(y), list, 1)     

start = time.time()

result = 0
# first, we define the range
for i in range(len(number)-13): 
    # and we calculate the product 
    test = prod(number[i:(i+13)])
    # and if the product is greater than result
    if test > result:
        # we assign it to the result itself
        result = test
        # and we store the list that gave us this result
        res_list = number[i:(i+13)]
    # and i is incremented


print(f'{result}  from {res_list}')

elapsed = (time.time() - start)
# result is 23514624000 from [5, 5, 7, 6, 6, 8, 9, 6, 6, 4, 8, 9, 5]
        
